sparql:
PREFIX dblp: <https://dblp.org/rdf/schema#>
PREFIX foaf: <http://xmlns.com/foaf/0.1/>
SELECT DISTINCT ?author1 ?author1Name ?author2 ?author2Name WHERE {
  ?paper1 dblp:title ?title1 .
  FILTER ( CONTAINS( LCASE( ?title1 ) , "a complete proof of global exponential convergence of a neural network for quadratic optimization with bound constraints" ) ) ?paper1 dblp:authoredBy ?author1 .
  OPTIONAL {
    ?author1 foaf:name ?author1Name
  }
  ?paper2 dblp:title ?title2 .
  FILTER ( CONTAINS( LCASE( ?title2 ) , "application of genetic algorithms for a tyre production scheduling information system" ) ) ?paper2 dblp:authoredBy ?author2 .
  OPTIONAL {
    ?author2 foaf:name ?author2Name
  }
  FILTER ( ?author1 = ?author2 )
}

